binding.script:
space(1, modifiers.getValue());
if (getprop("systems/audio/acp[0]/call_chan") == "hf1") {
	systems.HFS[0].pttToggle();
} elsif (getprop("systems/audio/acp[0]/call_chan") == "hf2") {
	systems.HFS[1].pttToggle();
}

